import {USER_SIGNIN_FAIL,USER_SIGNOUT, USER_SIGNIN_REQUEST, USER_SIGNIN_SUCCESS} from '../constants/userConstants';

export const userSigninReducer = (state={loading: true},action) =>{
    switch  (action.type) {
        case USER_SIGNIN_REQUEST:
            return {loading: true};
        case USER_SIGNIN_SUCCESS:
            return {loading:false,useInfo:action.payload};
        case USER_SIGNIN_FAIL:
                return {loading:false,error:action.payload};    
        case USER_SIGNOUT:
                    return {loading:false,error:action.payload};           

        default:
            return state;    
    }
}
